public class Stack<T> {
    private static final int DEFAULT_CAPACITY = 10;
    private T[] elements;
    private int size;

    // Use generic type T for flexibility
    public Stack() {
        this.elements = (T[]) new Object[DEFAULT_CAPACITY];
        this.size = 0;
    }

    // Method to add an element to the top of the stack
    public void push(T element) {
        ensureCapacity();
        elements[size++] = element;
    }

    // Method to remove and return the top element of the stack
    public T pop() {
        if (size == 0) {
            throw new IllegalStateException("Stack is empty");
        }
        T element = elements[--size];
        elements[size] = null; // Optional: help with garbage collection
        return element;
    }

    // Method to return the top element of the stack without removing it
    public T peek() {
        if (size == 0) {
            throw new IllegalStateException("Stack is empty");
        }
        return elements[size - 1];
    }

    // Method to check if the stack is empty
    public boolean isEmpty() {
        return size == 0;
    }

    // Method to get the number of elements in the stack
    public int size() {
        return size;
    }

    // Helper method to ensure the stack has enough capacity
    private void ensureCapacity() {
        if (size == elements.length) {
            grow();
        }
    }

    // Helper method to grow the array when necessary
    private void grow() {
        int newCapacity = elements.length * 2;
        T[] newElements = (T[]) new Object[newCapacity];
        System.arraycopy(elements, 0, newElements, 0, size);
        elements = newElements;
    }

    // Main method for testing
    public static void main(String[] args) {
        Stack<Integer> stack = new Stack<>();
        stack.push(1);
        stack.push(2);
        stack.push(3);

