public class Playlist {

    private List<String> songs;

    // Constructor
    public Playlist() {
        this.songs = new ArrayList<>();
    }

    // Method to add a song to the playlist
    public void addSong(String songTitle) {
        this.songs.add(songTitle);
        System.out.println("Added: " + songTitle + " to the playlist.");
    }

    // Method to remove a song from the playlist
    public void removeSong(String songTitle) {
        if (songs.contains(songTitle)) {
            this.songs.remove(songTitle);
            System.out.println("Removed: " + songTitle + " from the playlist.");
        } else {
            System.out.println(songTitle + " not found in the playlist.");
        }
    }

    // Method to get the number of songs in the playlist
    public int getSongCount() {
        return this.songs.size();
    }

    // Method to display the playlist
    public void displayPlaylist() {
        if (songs.isEmpty()) {
            System.out.println("Playlist is empty.");
            return;
        }

        System.out.println("Playlist:");
        for (String song : songs) {
            System.out.println("- " + song);
        }
    }

    // Method to check if a song is in the playlist
    public boolean hasSong(String songTitle) {
        return songs.contains(songTitle);
    }


