public class DoublyLinkedList {
    // Node class to represent each element in the DoublyLinkedList
    private static class Node {
        int data;
        Node prev;
        Node next;

        Node(int data) {
            this.data = data;
            this.prev = null;
            this.next = null;
        }
    }

    // Head of the DoublyLinkedList
    private Node head;
    // Tail of the DoublyLinkedList
    private Node tail;

    // Constructor to initialize an empty doubly linked list
    public DoublyLinkedList() {
        this.head = null;
        this.tail = null;
    }

    // Method to add a node at the beginning of the list
    public void addAtHead(int data) {
        Node newNode = new Node(data);
        if (head == null) {
            head = newNode;
            tail = newNode;
        } else {
            newNode.next = head;
            head.prev = newNode;
            head = newNode;
        }
    }

    // Method to add a node at the end of the list
    public void addAtEnd(int data) {
        Node newNode = new Node(data);
        if (tail == null) {
            head = newNode;
            tail = newNode;
        } else {
            newNode.prev = tail;
            tail.next = newNode;
            tail = newNode;
        }
    }

    // Method to delete a node based on value
    public void deleteByValue(int data) {
        Node current = head;
        while (current != null) {
            if (current.data == data) {
                if (current.prev != null) {
                    current.prev.next = current.next;
                } else {
                    head = current.next;
                }
                if (current.next != null) {
                    current.next.prev = current.prev;
                } else {
                    tail = current.prev;
                }
                return;
            }
            current = current.next;
        }
    }

    // Method to print the list from head to tail
    public void printList() {
        Node current = head;
        while (current != null) {
            System.out.print(current.data + " ");
            current = current.next;
        }
        System.out.println();
    }

    // Main method for testing
    public static void main(String[] args) {
        DoublyLinkedList list = new DoublyLinkedList();

        list.addAtHead(3);
        list.addAtHead(2);
        list.addAtHead(1);

        list.printList(); //Should print 1 2 3

        list.addAtEnd(4);
        list.addAtEnd(5);
        list.printList(); //Should print 1 2 3 4 5

        list.deleteByValue(3);
        list.printList(); //Should print 1 2 4 5

        list.deleteByValue(1);
        list.printList(); //Should print 2 4 5
    }
