public class ArrayStack<DataType> {
    private DataType[] array;
    private int top;

    public ArrayStack(int size) {
        array = (DataType[]) new Object[size];
        top = -1;
    }

    public void push(DataType data) {
        if (top < array.length) {
            array[++top] = data;
        } else {
            throw new RuntimeException("Stack is full");
        }
    }

    public DataType pop() {
        if (top >= 0) {
            DataType data = array[top];
            array[top--] = null;
            return data;
        } else {
            throw new RuntimeException("Stack is empty");
        }
    }

    public DataType peek() {
        if (top >= 0) {
            return array[top];
        } else {
            throw new RuntimeException("Stack is empty");
        }
    }

    public boolean isEmpty() {
        return top == -1;
    }

    public int size() {
        return top + 1;
    }
}
```

