from math import sqrt

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def generate_partitions(n, start=2, path=[]):
    if n == 1:
        yield path
    else:
        for i in range(start, int(sqrt(n)) + 1):
            if n % i == 0:
                yield from generate_partitions(n // i, i, path + [i])
        if path and n >= path[-1]:
            yield path + [n]

def score(partition):
    total_score = 0
    freq = {}
    for p in partition:
        if p not in freq:
            freq[p] = 1
        else:
            freq[p] += 1
    for p, f in freq.items():
        total_score += p ** f
    return total_score * len(partition)

