from itertools import combinations_with_replacement

def find_spec_partition(n, k, com):
    # Generate all possible partitions of n into k parts
    partitions = []

    # We use combinations_with_replacement to generate all possible combinations
    for part1 in range(1, n + 1):
        remaining = n - part1
        if remaining >= k - 1:
            for part2 in range(1, remaining + 1):
                if remaining - part2 >= k - 2:
                    for part3 in range(1, remaining - part2 + 1):
                        if remaining - part2 - part3 >= k - 3:
                            for part4 in range(1, remaining - part2 - part3 + 1):
