                                    # Continue similarly for higher k values
                                    # For simplicity, we assume k <= 4 here
                                    part5 = remaining - part2 - part3 - part4
                                    if part5 >= 1:
                                        partitions.append([part1, part2, part3, part4, part5])

    # Filter partitions to only have k parts
    partitions = [p for p in partitions if len(p) == k]

    # Calculate the product for each partition
    def product(part):
        result = 1
        for num in part:
            result *= num
        return result

    # Find the partition with the maximum or minimum product
    if com == 'max':
        max_product_partition = max(partitions, key=product)
    elif com == 'min':
        max_product_partition = min(partitions, key=product)
    else:
        raise ValueError("Command must be either 'max' or 'min'")

    # Return the partition in decreasing order
    return sorted(max_product_partition, reverse=True)

