    def words_below_hundred(num):
        if num == 0:
            return ""
        elif num < 20:
            return below_20[num]
        else:
            ten_part = num // 10
            unit_part = num % 10
            return tens[ten_part] + (" " + below_20[unit_part] if unit_part != 0 else "")

    def words_below_thousand(num):
        hundred_part = num // 100
        remainder = num % 100
        if hundred_part == 0:
            return words_below_hundred(remainder)
        else:
            return below_20[hundred_part] + " hundred" + (" " + words_below_hundred(remainder) if remainder != 0 else "")

