def find_spec_partition(n, k, com):
    """
    Find the partition of n with k terms that has the maximum or minimum product of its terms.

    Args:
        n (int): The number to be partitioned.
        k (int): The number of terms in the partition.
        com (str): The command, either'max' or'min'.

    Returns:
        list: The partition that has the maximum or minimum product value as an array with its terms in decreasing order.
    """

    # Initialize a dictionary to store the product of each partition
    products = {}

    # Generate all possible partitions of n with k terms
    for i in range(1, n // k + 1):
        if n % i == 0:
            # Calculate the remaining value after partitioning
            remaining = n // i

            # Generate all possible partitions of the remaining value
            for j in range(1, remaining // (k - 1) + 1):
                if remaining % j == 0:
                    # Calculate the product of the current partition
                    product = 1
                    for _ in range(k - 1):
                        product *= j
                    product *= i

                    # Store the product in the dictionary
                    products[i] = product

    # Find the maximum or minimum product depending on the command
    if com =='max':
        max_product = max(products.values())
        max_partition = [i for i in products if products[i] == max_product]
        return sorted(max_partition, reverse=True)
    elif com =='min':
        min_product = min(products.values())
        min_partition = [i for i in products if products[i] == min_product]
        return sorted(min_partition, reverse=True)
    else:
        raise ValueError("Invalid command. Please use'max' or'min'.")

