    if n % i == 0
      # Calculate the remaining value after partitioning
      remaining = n / i

      # Generate all possible partitions of the remaining value
      (1..remaining / (k - 1)).each do |j|
        if remaining % j == 0
          # Calculate the product of the current partition
          product = 1
          (k - 1).times do
