from typing import List

class Solution:
    def shortestSuperstring(self, A: List[str]) -> str:
        n = len(A)
        dp = [[0] * n for _ in range(n)]
        for i in range(n):
            dp[i][i] = len(A[i])

        for length in range(1, n):
            for i in range(n - length):
                j = i + length
                max_len = 0
                for k in range(i, j):
                    lce = self.lce(A[i], A[k + 1])
                    if lce > max_len:
                        max_len = lce
                        dp[i][j] = max(dp[i][j], max_len + dp[k + 1][j])

        max_len = max(dp[i][n - 1] for i in range(n))
        index = [i for i in range(n) if dp[i][n - 1] == max_len][0]

        result = []
        while index!= n - 1:
            result.append(A[index])
            for j in range(index + 1, n):
                if dp[index][j] == dp[index][n - 1]:
                    index = j
                    break

        result.append(A[index])
        return ''.join(reversed(result))

    def lce(self, s1, s2):
        m, n = len(s1), len(s2)
        i, j = 0, 0
        while i < m and j < n:
            if s1[i] == s2[j]:
