using namespace std;

struct Edge {
    int u, v, w;
};

struct Vertex {
    int id, a, b, d;
    Vertex(int id, int a, int b) : id(id), a(a), b(b), d(0) {}
};

struct Compare {
    bool operator()(const Vertex& a, const Vertex& b) {
        return a.d > b.d;
    }
};

int main() {
    int N, M;
    cin >> N >> M;

    vector<Edge> edges(M);
    for (int i = 0; i < M; i++) {
        cin >> edges[i].u >> edges[i].v;
    }

    vector<Vertex> vertices(N);
    for (int i = 0; i < N; i++) {
        cin >> vertices[i].a >> vertices[i].b;
        vertices[i].id = i + 1;
    }

    for (int i = 0; i < M; i++) {
        edges[i].w = i + 1;
    }

    sort(vertices.begin(), vertices.end(), [](const Vertex& a, const Vertex& b) {
        return a.a < b.a;
    });

    vector<vector<int>> g(N);
    for (int i = 0; i < M; i++) {
        g[edges[i].u - 1].push_back(edges[i].v - 1);
        g[edges[i].v - 1].push_back(edges[i].u - 1);
    }

    vector<int> parent(N, -1);
    vector<int> rank(N, 0);

    function<int(int)> find = [&](int x) {
        if (parent[x] == -1) return x;
        return parent[x] = find(parent[x]);
    };

    function<void(int, int)> union_ = [&](int x, int y) {
        x = find(x);
        y = find(y);
        if (x == y) return;
        if (rank[x] < rank[y]) swap(x, y);
        parent[y] = x;
        if (rank[x] == rank[y]) rank[x]++;
    };

    vector<bool> visited(N, false);

    int W = 0;
    for (int i = 0; i < N; i++) {
        if (visited[i]) continue;
        queue<Vertex> q;
        q.push(vertices[i]);
        visited[i] = true;
        while (!q.empty()) {
            Vertex u = q.front();
            q.pop();
            for (int v : g[u.id - 1]) {
                if (visited[v]) continue;
                int cost = max(0, u.a - vertices[v].b);
                if (u.d + cost <= vertices[v].a) {
                    q.push(vertices[v]);
                    visited[v] = true;
                    parent[v] = u.id - 1;
                    vertices[v].d = u.d + cost;
                }
            }
        }

        int max_d = 0;
        for (int i = 0; i < N; i++) {
            if (!visited[i]) continue;
            max_d = max(max_d, vertices[i].d);
        }

        W = max(W, max_d);
    }

    cout << W << endl;

    return 0;
}
