from collections import defaultdict
import sys

def solve(s):
    n = len(s)
    visited = defaultdict(int)
    x, y = 0, 0
    stack = []
    for i in range(n):
        c = s[i]
        if c == 'L':
            x -= 1
        elif c == 'R':
            x += 1
        elif c == 'U':
            y += 1
        else:
            y -= 1
        if visited[(x, y)]:
            if (x, y)!= (0, 0):
                return 0
            if stack and stack[-1] == c:
                stack.pop()
            else:
                return 0
        else:
            visited[(x, y)] = 1
            stack.append(c)
    return len(stack)

def main():
    input = sys.stdin.readline
    output = sys.stdout.write
    t = int(input())
    for _ in range(t):
        s = input().strip()
        ans = solve(s)
        output(str(ans) + "\n")
        if ans > 0:
            stack = []
            for c in s:
                if c == 'L':
                    stack.append(c)
                    stack.append(c)
                elif c == 'R':
                    stack.append(c)
                    stack.append(c)
                elif c == 'U':
                    stack.append(c)
                    stack.append(c)
                else:
                    stack.append(c)
                    stack.append(c)
            stack = stack[:ans*2]
            output("".join(stack) + "\n")

if __name__ == "__main__":
