def min_moves(s, c):
    n = len(s)
    if n == 1:
        return 0 if s == c else 1
    mid = n // 2
    left = s[:mid]
    right = s[mid:]
    if left.count(c) == mid:
        return min_moves(right, chr(ord(c) + 1))
    elif right.count(c) == mid:
        return min_moves(left, chr(ord(c) + 1))
    else:
        return mid - left.count(c) + mid - right.count(c)

t = int(input())
