```python
import enum

class Direction(enum.Enum):
    UP = 8
    DOWN = 2
    LEFT = 4
    RIGHT = 6

class Input:
    STATES = {
        Direction.UP: False,
        Direction.DOWN: False,
        Direction.LEFT: False,
        Direction.RIGHT: False
    }

    @staticmethod
    def get_state(direction):
        return Input.STATES[direction]

class Player:
    def __init__(self, x, y):
        self.tile = Tile(x, y)
        self.direction = None
        self.velocity = [0, 0]

    def update(self):
        # Check for pressed keys
        pressed_keys = {
            Direction.UP: Input.get_state(Direction.UP),
            Direction.DOWN: Input.get_state(Direction.DOWN),
            Direction.LEFT: Input.get_state(Direction.LEFT),
            Direction.RIGHT: Input.get_state(Direction.RIGHT)
        }

        # Check if a new key is pressed
        if any(pressed_keys.values()):
            # Find the key with the highest precedence
            precedence = [Direction.UP, Direction.DOWN, Direction.LEFT, Direction.RIGHT]
            current_key = None
            for key in precedence:
                if pressed_keys[key]:
                    current_key = key
                    break

            # If the key is pressed for the first time, change direction without moving
            if current_key!= self.direction:
                self.direction = current_key
                self.velocity = [0, 0]

            # If the key is still being pressed, move towards the current direction
            if pressed_keys[current_key]:
                if current_key == Direction.UP:
                    self.velocity = [0, 1]
                elif current_key == Direction.DOWN:
                    self.velocity = [0, -1]
                elif current_key == Direction.LEFT:
                    self.velocity = [-1, 0]
                elif current_key == Direction.RIGHT:
                    self.velocity = [1, 0]

                # Move the player
                self.tile.x += self.velocity[0]
                self.tile.y += self.velocity[1]

        # If the current key is released, check for the previous key
        elif self.direction is not None:
            # Find the previous key
            precedence = [Direction.UP, Direction.DOWN, Direction.LEFT, Direction.RIGHT]
            previous_key = None
            for key in precedence:
                if key!= self.direction and Input.get_state(key):
                    previous_key = key
                    break

            # If a previous key is found, change direction to it
            if previous_key is not None:
                self.direction = previous_key
                self.velocity = [0, 0]

        # If all keys are released, stand still
        else:
            self.velocity = [0, 0]

    def __str__(self):
        return f"Player at {self.tile.x, self.tile.y} with direction {self.direction}"

class Tile:
    def __init__(self, x, y):
        self._x = x
        self._y = y

    @property
    def x(self):
        return self._x

    @property
    def y(self):
        return self._y

    def __str__(self):
        return f"({self._x},{self._y})"

# Example usage:
player = Player(0, 0)
while True:
    print("Inputs:")
    # Get user input
    # For example:
    # Input.get_state(Direction.UP) = True
    # Input.get_state(Direction.DOWN) = False
    #...
    # Update the player
    player.update()
    print("Outputs:")
    print(player)
    print()
```

