def get_column_title(num):
    """
    Convert a decimal number to an Excel column title.

    Args:
    num (int): The decimal index of the Excel column.

    Returns:
    str: The title of the Excel column.

    Raises:
    TypeError: If the input is not an integer.
    IndexError: If the input is less than 1.
    """

    # Check if the input is an integer
    if not isinstance(num, int):
        raise TypeError("Input must be an integer.")

    # Check if the input is less than 1
    if num < 1:
        raise IndexError("Input must be a natural number.")

    result = ""
    while num > 0:
        # Calculate the remainder of num divided by 26
        remainder = num % 26

        # If remainder is 0, set it to 26 and subtract 26 from num
        if remainder == 0:
            remainder = 26
            num -= 26

        # Prepend the corresponding character to the result
        result = chr(64 + remainder) + result

        # Update num by performing integer division by 26
        num //= 26

    return result
