def is_prime(n):
    """Check if a number is prime."""
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def goldbach(n):
    """Find all identical pairs of prime numbers for a given even number."""
    pairs = []
    for i in range(2, n // 2 + 1):
        if is_prime(i) and is_prime(n - i):
            pairs.append([i, n - i])
    return pairs

