class Graph:
    def __init__(self, edges):
        self.V = 26
        self.adj_list = [[] for _ in range(self.V)]
        self.mod = 1000000007
        self.build_graph(edges)

    def build_graph(self, edges):
        for i, row in enumerate(edges):
            for j, edge in enumerate(row):
                if edge == 1:
                    self.adj_list[i].append(j)

    def dfs(self, node, visited, path):
        if node == ord('z') - ord('a'):
            return 1
        visited.add(node)
        count = 0
        for neighbor in self.adj_list[node]:
            if neighbor not in visited:
                count += self.dfs(neighbor, visited, path)
                count %= self.mod
        visited.remove(node)
        return count

    def count_words(self, char, length):
        visited = set()
        return self.dfs(ord(char) - ord('a'), visited, visited)


def main():
