def is_weakly_connected(edges):
    # Create a graph from the edges
    graph = {}
    for u, v in edges:
        if u not in graph:
            graph[u] = []
        graph[u].append(v)

    # Check if the graph is strongly connected
    visited = set()
    for u in graph:
        if u not in visited:
            if not is_strongly_connected(graph, u, visited):
                return False

    return True


def is_strongly_connected(graph, vertex, visited):
    # Check if the vertex is in the visited set
    if vertex in visited:
        return True

    # Add the vertex to the visited set
    visited.add(vertex)

    # Check if all neighbors of the vertex are in the visited set
    for neighbor in graph[vertex]:
        if neighbor not in visited:
            if not is_strongly_connected(graph, neighbor, visited):
                return False

    return True


