from typing import List

class Solution:
    def reachableNodes(self, edges: List[List[int]], M: int, N: int) -> int:
        graph = {}
        for i, j, n in edges:
            if i not in graph:
                graph[i] = []
            if j not in graph:
                graph[j] = []
            graph[i].append((j, n))
            graph[j].append((i, n))

        visited = set()
        queue = [(0, 0)]  # (node, moves)

        while queue:
            node, moves = queue.pop(0)
            if node not in visited:
                visited.add(node)
                for neighbor, new_moves in graph[node]:
                    queue.append((neighbor, moves + new_moves))
                    if moves + new_moves <= M:
                        return moves + new_moves

        return -1  # No reachable nodes in M moves
