def stoneGame(piles):
    n = len(piles)
    dp = [0] * n
    for i in range(n):
        for j in range(i, n):
            dp[i] = max(dp[i], dp[j] + piles[j] - piles[i])

    result = [0] * n
    for i in range(n - 1, -1, -1):
        result[i] = dp[i] - piles[i] + 1

    for num in range(1, n + 1):
        if result[num - 1] == 0:
            continue
        for i in range(num - 1, 0, -1):
            result[i] += result[i - 1]

    return [num for num, count in zip(result, dp) if count > 0]


