    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def next_greater MagicalNumber(n):
    """Find the next greater magical number."""
    # Find prime numbers up to n
    primes = [i for i in range(2, n) if is_prime(i)]

    # Find the next greater magical number
    for i in range(len(primes) - 1):
        if primes[i] < n:
            for j in range(i + 1, len(primes)):
                if primes[j] > primes[i]:
                    return primes[j] + primes[i]

    # If no greater magical number is found, return n
    return n

