class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end_of_word = False

class Solution:
    def shortestSuperstring(self, A: List[str]) -> str:
        root = TrieNode()

        # Insert all strings into the Trie
        for word in A:
            node = root
            for char in word:
                if char not in node.children:
                    node.children[char] = TrieNode()
                node = node.children[char]
            node.is_end_of_word = True

        # Find the shortest string that contains each prefix
        result = ""
        for word in A:
            node = root
            for char in word:
                node = node.children[char]
            if node.is_end_of_word:
                result = word
                break

        return result
