        # Initialize the result list
        result = []

        # Iterate over each point
        for point in points:
            # Calculate the Euclidean distance from the point to the origin
            distance = point[0]**2 + point[1]**2

            # If the result list is empty or the current point is closer than the last point in the result list
            if not result or distance < self._heapq.heappop(result):
                # Push the point into the result list
                self._heapq.heappush(result, (distance, point))

        # Return the K closest points
        return [point for _, point in result[:K]]

    def _kdtree(self, points: List[List[int]], K: int) -> KdTree:
        # Initialize the K-D Tree
        self.kdtree = KdTree(points)

        # Initialize the K-D Tree nodes
        nodes = {}

        # Iterate over each point
