            # Calculate the Euclidean distance from the point to the origin
            distance = point[0]**2 + point[1]**2

            # Find the node in the K-D Tree that contains the point
            node = self._find_node(nodes, distance)

            # If the node is not found, create a new node
            if node is None:
                nodes[distance] = Node(point)
            else:
                # Insert the point into the node
                node.insert(point)

        return self.kdtree

    def _find_node(self, nodes: dict, distance: int) -> Node:
        # Initialize the current node
        node = None

        # Iterate over the nodes
        for key, value in nodes.items():
            # If the distance is less than the current distance, update the current node
            if key < distance:
                node = value
            # If the distance is equal to the current distance, update the current node
            elif key == distance:
                node = value

        return node

    class Node:
        def __init__(self, point: List[int]):
            self.point = point
            self.left = None
            self.right = None

        def insert(self, point: List[int]):
            # If the left child is None, create a new node
            if self.left is None:
                self.left = self.Node(point)
            # If the right child is None, create a new node
            elif self.right is None:
                self.right = self.Node(point)
            # If the point is less than the current point, insert into the left child
            elif point[0] < self.point[0]:
                if self.left.left is None:
                    self.left.left = self.Node(point)
                else:
                    self.left.insert(point)
            # If the point is greater than the current point, insert into the right child
            else:
                if self.right.right is None:
                    self.right.right = self.Node(point)
                else:
                    self.right.insert(point)

        def __lt__(self, other: 'Node'):
            # Compare the distances from the left and right children
            if self.point[0] < other.point[0]:
                return True
            elif self.point[0] > other.point[0]:
                return False
            # If the distances are equal, compare the distances from the left and right children
            else:
                return self.point[0] < other.point[0]
