            if char == '1':
                first_half_count += 1
            else:
                break

        # Iterate over the second half
        for char in second_half:
            if char == '1':
                second_half_count += 1
            else:
                break

        # Calculate the number of ways to split the string into three parts with the same number of '1's
        ways = 0
        if first_half_count == 0 and second_half_count == 0:
            return 1
        if first_half_count == 0:
            return 0
        if second_half_count == 0:
            return 0

        # Calculate the number of ways to choose the middle character
        for i in range(1, len(s)//2 + 1):
            if first_half_count == 0:
                break
            if second_half_count == 0:
                break
            if i == 1:
                ways += first_half_count
