            if i == 2:
                ways += second_half_count - 1
                break
            if second_half_count == 1:
                ways += second_half_count - 1
                break
            ways += second_half_count - 2
            second_half_count -= 1
        ways %= MOD

        # Calculate the number of ways to choose the middle character for the second half
        for i in range(1, len(s)//2 + 1):
            if third_half == [] or i == 1:
                ways += third_half
            elif i == 2:
                ways += third_half - 1
            elif i == 3:
                ways += third_half - 2
            elif i == 4:
                ways += third_half - 3
            else:
                ways += third_half - 4
            third_half = []
        ways %= MOD

        return ways
