def is_prime(n):
    """Check if a number is prime."""
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def goldbach(even_number):
    """Find all pairs of primes that sum up to an even number."""
    if even_number % 2!= 0:
        raise ValueError("Even number must be even.")

    prime_pairs = []
    for i in range(2, even_number // 2 + 1):
        if is_prime(i) and is_prime(even_number - i):
            pair = [i, even_number - i]
            prime_pairs.append(pair)

    return prime_pairs

