from collections import defaultdict

def is_weakly_connected(n, edges):
    """Check if the graph is weakly connected."""
    adj = defaultdict(list)
    for i in range(n):
        adj[edges[i]].append(i + 1)

    visited = [False] * (n + 1)
    stack = [1]
    visited[1] = True

    while stack:
        vertex = stack.pop()
        for neighbor in adj[vertex]:
            if not visited[neighbor]:
                stack.append(neighbor)
                visited[neighbor] = True

    return all(visited[1:])

def has_cycle(n, edges):
    """Check if the graph has a cycle."""
    adj = defaultdict(list)
    for i in range(n):
        adj[i + 1].append(edges[i])

    visited = [False] * (n + 1)
    recursion_stack = [False] * (n + 1)

    for vertex in range(1, n + 1):
        if not visited[vertex]:
            if has_cycle_util(vertex, adj, visited, recursion_stack):
                return True

    return False

def has_cycle_util(vertex, adj, visited, recursion_stack):
    """Utility function to check if the graph has a cycle."""
    visited[vertex] = True
    recursion_stack[vertex] = True

    for neighbor in adj[vertex]:
        if not visited[neighbor]:
            if has_cycle_util(neighbor, adj, visited, recursion_stack):
                return True
        elif recursion_stack[neighbor]:
            return True

    recursion_stack[vertex] = False
    return False

def main():
    n = int(input())
    edges = list(map(int, input().split()))

    if not is_weakly_connected(n, edges):
        print("IMPOSSIBLE")
        return

    if has_cycle(n, edges):
        print("POSSIBLE")
    else:
        print("IMPOSSIBLE")

if __name__ == "__main__":
    main()

