    def longestSubstring(self, s: str, k: int) -> int:
        def is_valid(substring: str, k: int) -> bool:
            """Check if every character in the substring appears at least k times."""
            char_count = {}
            for char in substring:
                char_count[char] = char_count.get(char, 0) + 1
            return all(count >= k for count in char_count.values())

        def longest_substring(s: str, k: int) -> int:
            """Find the length of the longest substring that meets the condition."""
            max_length = 0
            for i in range(len(s)):
                for j in range(i + 1, len(s) + 1):
                    substring = s[i:j]
                    if is_valid(substring, k):
                        max_length = max(max_length, len(substring))
            return max_length

        return longest_substring(s, k)


