    def longestSubstring(self, s: str, k: int) -> int:
        def longest_substring(s: str, k: int) -> int:
            """Find the length of the longest substring that meets the condition."""
            if len(s) < k:
                return 0

            for char in set(s):
                if s.count(char) < k:
                    return max(longest_substring(sub, k) for sub in s.split(char))

            return len(s)

        return longest_substring(s, k)


