class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x]!= x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x!= root_y:
            if self.rank[root_x] > self.rank[root_y]:
                self.parent[root_y] = root_x
            elif self.rank[root_x] < self.rank[root_y]:
                self.parent[root_x] = root_y
            else:
                self.parent[root_y] = root_x
                self.rank[root_x] += 1

def min_cost(n, m, languages):
    uf = UnionFind(n)
    group_languages = [[] for _ in range(n)]
    for i in range(n):
        for language in languages[i]:
            group_languages[i].append(language)

    for i in range(n):
        for j in range(i + 1, n):
            common_language = set(languages[i]) & set(languages[j])
            if common_language:
                uf.union(i, j)

    groups = {}
    for i in range(n):
        root = uf.find(i)
        if root not in groups:
            groups[root] = []
        groups[root].append(i)

