def square_sums_row(n):
    def is_square(num):
        root = math.sqrt(num)
        return int(root + 0.5) ** 2 == num

    def backtrack(start, path):
        if len(path) == n:
            return path if all(is_square(path[i] + path[i+1]) for i in range(n-1)) else None
        for num in range(1, n+1):
