from collections import deque

class Solution:
    def shortestBridge(self, A: List[List[int]]) -> int:
        n = len(A)
        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        island1 = []
        visited = [[False] * n for _ in range(n)]

        # Find the first island
        def dfs(i, j):
            if 0 <= i < n and 0 <= j < n and A[i][j] == 1 and not visited[i][j]:
                visited[i][j] = True
                island1.append((i, j))
                for di, dj in directions:
                    dfs(i + di, j + dj)

        # Find the first island
        found = False
        for i in range(n):
            if found:
                break
            for j in range(n):
                if A[i][j] == 1:
                    dfs(i, j)
                    found = True
                    break

        # Perform BFS to find the shortest bridge
        queue = deque([(x, y, 0) for x, y in island1])
        while queue:
            x, y, dist = queue.popleft()
            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if 0 <= nx < n and 0 <= ny < n:
                    if A[nx][ny] == 1 and (nx, ny) not in island1:
                        return dist
                    elif A[nx][ny] == 0 and (nx, ny) not in island1:
                        queue.append((nx, ny, dist + 1))
                        island1.append((nx, ny))
        return -1
