def calculate_distance(x, y):
    return math.sqrt(x**2 + y**2)

def solve(n, vectors):
    x, y = 0, 0
    result = []
    for vector in vectors:
        vx, vy = vector
        if calculate_distance(x + vx, y + vy) <= calculate_distance(x - vx, y - vy):
            x += vx
            y += vy
            result.append(1)
        else:
            x -= vx
            y -= vy
            result.append(-1)
    return result

def main():
    n = int(input())
    vectors = []
    for _ in range(n):
        x, y = map(int, input().split())
        vectors.append((x, y))
    result = solve(n, vectors)
    print(*result)

if __name__ == "__main__":
    main()
