import math

def is_palindrome(s):
    return s == s[::-1]

def count_exponential_palindromes(s):
    n = len(s)
    count = 0
    for i in range(1, n+1):
        count += 1  # single element sequence is always exponential
        for p in range(2, n+1):
            j = i * p
            if j <= n:
                seq = s[i-1] + s[j-1]
                if is_palindrome(seq):
                    count += 1
                    k = j * p
                    if k <= n:
                        seq += s[k-1]
                        if is_palindrome(seq):
                            count += 1
    return count

