    queue = deque([(x, y, 0)])  # (x, y, moves)
    visited[x][y] = 0

    while queue:
        x, y, moves = queue.popleft()

        # Check all possible moves
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy

            # Check if the new position is within the grid and not blocked
