def check_updown(arr):
    n = len(arr)
    for i in range(n-1):
        if i % 2 == 0 and arr[i] > arr[i+1]:
            return False
        if i % 2 == 1 and arr[i] < arr[i+1]:
            return False
    return True

def solve(n, arr):
    max_len = 0
    for i in range(n):
        for j in range(i+1, n+1):
            sub_arr = arr[i:j]
            if check_updown(sub_arr):
                max_len = max(max_len, len(sub_arr))

    for i in range(n-1):
        for num in range(min(arr[i], arr[i+1])+1, max(arr[i], arr[i+1])):
            new_arr = arr[:i+1] + [num] + arr[i+1:]
            for j in range(len(new_arr)):
                for k in range(j+1, len(new_arr)+1):
                    sub_arr = new_arr[j:k]
                    if check_updown(sub_arr):
                        max_len = max(max_len, len(sub_arr))
    return max_len

