def main():
    N, k = map(int, input().split())
    V = list(map(int, input().split()))
    B = list(map(int, input().split()))

    ans = 0
    for mask in range(1 << N):
        brackets = []
        values = []
        for i in range(N):
            if (mask >> i) & 1:
                brackets.append(B[i])
                values.append(V[i])
        if not is_well_bracketed(brackets, k):
            continue
        ans = max(ans, sum(values))

    print(ans)

def is_well_bracketed(brackets, k):
    stack = []
    for bracket in brackets:
        if bracket <= k:
            stack.append(bracket)
        else:
            if not stack:
                return False
            opening_bracket = stack.pop()
            if opening_bracket!= bracket - k:
                return False
    return not stack

