def is_prime(num):
    """Check if a number is prime."""
    if num < 2:
        return False
    for i in range(2, int(num**0.5) + 1):
        if num % i == 0:
            return False
    return True


def goldbach(n):
    """Find all pairs of prime numbers that add up to an even number."""
    if n <= 2:
        return []

    # Generate all possible pairs of numbers that add up to n
    pairs = []
    for i in range(2, n):
        j = n - i
        # Check if both numbers are prime
        if is_prime(i) and is_prime(j):
            # Ensure pairs are in descending order and all pairs are in ascending order based on the first element
            if i <= j:
                pairs.append([i, j])

    # Sort pairs based on the first element
    pairs.sort(key=lambda x: x[0])

    return pairs


