def find_min_hours(n, plants):
    min_hours = float('inf')
    for i in range(n):
        for j in range(i + 1, n):
            diff = plants[i] - plants[j]
            hours = diff // (j - i + 1)
            if diff % (j - i + 1) == 0:
                hours = max(0, hours)
            else:
                hours = max(0, hours) + 1
            min_hours = min(min_hours, hours)
    return min_hours

