class Player:
    def __init__(self, x, y):
        self.position = Tile(x, y)
        self.direction = None
        self.prev_directions = set()
        self.key_states = {Direction.UP: False, Direction.DOWN: False, Direction.LEFT: False, Direction.RIGHT: False}

    def update(self):
        # Get the current key states
        current_key_states = Input.get_state_dict()

        # Update the key_states dict
        for direction, state in current_key_states.items():
            if direction in self.key_states:
                self.key_states[direction] = state

        # Check for key presses
        for direction, state in self.key_states.items():
            if not state:
                continue

            if direction not in self.prev_directions:
                # This key is being pressed for the first time
                self.direction = direction
                self.prev_directions.add(direction)
                break

        # Check for key releases
        for direction in self.prev_directions:
            if direction not in self.key_states or not self.key_states[direction]:
                # This key is not being pressed anymore
                self.prev_directions.remove(direction)

                if not self.prev_directions:
                    # No keys are being pressed, so the player will stand still
                    self.direction = None
                elif len(self.prev_directions) == 1:
                    # Only one key is being pressed, so the player will move
                    # in that direction
                    self.direction = list(self.prev_directions)[0]
                else:
                    # Multiple keys are being pressed, so the player will
                    # prioritize the direction of the first key in the order
                    # [UP, DOWN, LEFT, RIGHT]
                    prev_directions_sorted = sorted(self.prev_directions, key=lambda d: (d in [Direction.UP, Direction.DOWN], d in [Direction.UP, Direction.DOWN, Direction.LEFT]))
                    self.direction = prev_directions_sorted[0]

        # Move the player if a direction is set
