#include <bits/stdc++.h>
using namespace std;

// Function to find the number of undamaged leaves
long long undamaged_leaves(long long N, vector<long long> caterpillar_length) {
    // Initialize undamaged leaves as true
    bool undamaged[N + 1];
    fill(undamaged, undamaged + N + 1, true);

    // Iterate through the caterpillars
    for (int i = 0; i < caterpillar_length.size(); i++) {
        // Calculate the caterpillar's steps
        long long steps = (N / caterpillar_length[i]) + (N % caterpillar_length[i] > 0);

        // Iterate through the steps
        for (int j = 0; j < steps; j++) {
            long long leaf = 1 + j * caterpillar_length[i];
            if (leaf <= N) {
                undamaged[leaf] = false;
            }
        }
    }

    // Count and return the number of undamaged leaves
    long long count = 0;
    for (int i = 1; i <= N; i++) {
        if (undamaged[i]) {
            count++;
        }
    }
    return count;
}

