**`KanbanBoard.js`**

```javascript
import React, { useState } from 'react';
import Column from './Column';
import { DragDropContext } from 'react-beautiful-dnd';
import styled from 'styled-components';

const BoardContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-start; // or space-around, etc.
  padding: 20px;
`;

const initialData = {
  columns: [
    {
      id: 'todo',
      title: 'To Do',
      tasks: [],
    },
    {
      id: 'inprogress',
      title: 'In Progress',
      tasks: [],
    },
    {
      id: 'done',
      title: 'Done',
      tasks: [],
    },
  ],
};

function KanbanBoard() {
  const [columns, setColumns] = useState(initialData.columns);

  const onDragEnd = (result) => {
    if (!result.destination) {
      return; // Dropped outside the board
    }

    const sourceColumnId = result.source.droppableId;
    const destinationColumnId = result.destination.droppableId;

    if (sourceColumnId === destinationColumnId) {
      return; // No movement needed
    }

    const sourceColumn = columns.find((col) => col.id === sourceColumnId);
    const destinationColumn = columns.find((col) => col.id === destinationColumnId);

    const task = sourceColumn.tasks[result.source.index];
    sourceColumn.tasks.splice(result.source.index, 1);
    destinationColumn.tasks.splice(result.destination.index, 0, task);

    setColumns([...columns]);
  };


  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <BoardContainer>
        {columns.map((column) => (
          <Column key={column.id} column={column} />
        ))}
      </BoardContainer>
    </DragDropContext>
  );
}

export default KanbanBoard;
```

