using System;
using System.Collections.ObjectModel;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Media;
using System.Windows.Threading;

namespace StopwatchApp
{
    public partial class MainPage : Page
    {
        private DispatcherTimer timer;
        private TimeSpan elapsedTime;
        private ObservableCollection<string> lapTimes = new ObservableCollection<string>(); //Use ObservableCollection
        private bool isRunning;

        public ObservableCollection<string> LapTimes { get { return lapTimes; } } //Expose the collection for data binding

        public MainPage()
        {
            InitializeComponent();

            timer = new DispatcherTimer();
            timer.Interval = TimeSpan.FromSeconds(0.0166666667); // ~60 fps
            timer.Tick += Timer_Tick;
            isRunning = false;  //Start in paused state
        }

        private void StartButton_Click(object sender, RoutedEventArgs e)
        {
            StartStopwatch();
        }

        private void PauseButton_Click(object sender, RoutedEventArgs e)
        {
            StopStopwatch();
        }

        private void ResetButton_Click(object sender, RoutedEventArgs e)
        {
            ResetStopwatch();
        }

        private void LapButton_Click(object sender, RoutedEventArgs e)
        {
            TakeLap();
        }

        private void StartStopwatch()
        {
            isRunning = true;
            StartButton.IsEnabled = false;
            PauseButton.IsEnabled = true;
            ResetButton.IsEnabled = false;
            LapButton.IsEnabled = true;
            timer.Start();
        }

        private void StopStopwatch()
        {
            isRunning = false;
            StartButton.IsEnabled = true;
            PauseButton.IsEnabled = false;
            ResetButton.IsEnabled = true;
            LapButton.IsEnabled = true;
            timer.Stop();
        }

        private void ResetStopwatch()
        {
            StopStopwatch();
            elapsedTime = TimeSpan.Zero;
            TimeDisplay.Text = "00:00:00";
            lapTimes.Clear();
        }

        private void Timer_Tick(object sender, EventArgs e)
        {
            if (isRunning)
            {
                elapsedTime += TimeSpan.FromTicks(timer.Interval.Ticks); // Use ticks for accuracy
                TimeDisplay.Text = elapsedTime.ToString(@"mm\:ss\.fff"); // Format for milliseconds
            }
        }

        private void TakeLap()
        {
            string lapTime = elapsedTime.ToString(@"mm\:ss\.fff");
            lapTimes.Add(lapTime);
        }
    }
}
```

