        {
            StartButton.Visibility = Visibility.Collapsed;
            PauseButton.Visibility = Visibility.Visible;
            ResetButton.Visibility = Visibility.Collapsed;
            timer.Start();
            elapsedMilliseconds = 0;   //Reset time when starting again
