        }

        private void PauseStopwatch()
        {
            StartButton.Visibility = Visibility.Visible;
            PauseButton.Visibility = Visibility.Collapsed;
            ResetButton.Visibility = Visibility.Visible;
            timer.Stop();
        }

        private void ResetStopwatch()
        {
