```java
import android.os.Bundle;
import android.util.Log;
import androidx.appcompat.app.AppCompatActivity;
import java.net.URI;
import java.net.URISyntaxException;
import io.reactivex.Observable;
import io.reactivex.android.schedulers.AndroidSchedulers;
import io.reactivex.schedulers.Schedulers;
import okhttp3.OkWebSocket;
import okhttp3.Request;
import okhttp3.Response;
import okhttp3.WebSocketListener;

public class MainActivity extends AppCompatActivity {

    private OkWebSocket webSocket;
    private final String websocketURL = "ws://your-backend-url/websocket"; // Replace with your backend URL

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        try {
            URI uri = new URI(websocketURL);
            Request request = new Request.Builder()
                    .url(uri)
                    .build();

            webSocket = new OkWebSocket(request, new MyWebSocketListener());
            webSocket.connect(Observable.timer(5, java.util.concurrent.TimeUnit.SECONDS)
                    .subscribeOn(Schedulers.io())
                    .observeOn(AndroidSchedulers.mainThread())); // Retry after 5 seconds
        } catch (URISyntaxException e) {
            Log.e("WebSocket", "Invalid URI: " + e.getMessage());
        }
    }

    @Override
    protected void onDestroy() {
        if (webSocket != null) {
            webSocket.close(1000, null); // Normal close
        }
        super.onDestroy();
    }

    private class MyWebSocketListener extends WebSocketListener {
        @Override
        public void onOpen(Response response) {
            Log.d("WebSocket", "Connection opened: " + response.code());
            // Subscribe to topics here
            subscribeToTopic("your-topic-name");
        }

        @Override
        public void onMessage(String text) {
            Log.d("WebSocket", "Received message: " + text);
            // Process the message (update UI, etc.)
