  const [isCameraOpen, setIsCameraOpen] = useState(false);

  const openCamera = async () => {
    if (Platform.OS === 'ios') {
      // iOS Camera Handling
      try {
        await UIManager.openCamera(
          {
            reason: 'use-existing', // Recommended to use existing camera if possible
          },
          (error) => {
            if (error) {
              console.error("Error opening camera: ", error);
            } else {
              console.log("Camera opened successfully!");
            }
          }
        );
        setIsCameraOpen(true);
      } catch (error) {
        console.error("Error opening camera: ", error);
      }

    } else if (Platform.OS === 'android') {
      //Android Camera Handling - Using CameraView
      try{
        import('expo-camera').then((Camera) => {
          const { CameraView } = Camera;
          setIsCameraOpen(true);
          // Open Camera
        });
      } catch (e){
        console.log("Android Camera Error", e);
      }


    }
  };

  return (
    <View style={styles.container}>
      <Button
        title="Open Camera"
        onPress={openCamera}
        color="#007AFF" // A nice blue color
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    padding: 20,
  },
});

export default CameraButton;
```

