```javascript
const XLSX = require('xlsx');

/**
 * Reads an Excel file and returns its data as an array of arrays.
 *
 * @param {string} filePath The path to the Excel file.
 * @param {string} sheetName (Optional) The name of the sheet to read.  If not provided, reads the first sheet.
 * @returns {Promise<Array<Array<any>>>} A promise that resolves to an array of arrays representing the Excel data.
 *                                       Rejects if there's an error reading the file or sheet.
 */
async function readExcel(filePath, sheetName) {
  try {
    const workbook = XLSX.readFile(filePath);

    // Determine which sheet to read
    const sheet = sheetName ? workbook.Sheets[sheetName] : workbook.Sheets[workbook.SheetNames[0]];

    if (!sheet) {
      throw new Error(`Sheet "${sheetName || 'first sheet'}" not found in the Excel file.`);
    }

    // Convert the sheet data to an array of arrays
    const data = XLSX.utils.sheet_to_json(sheet, { header: 1 });  // header: 1 assumes the first row contains headers
    return data;

  } catch (error) {
    console.error("Error reading Excel file:", error);
    throw error; // Re-throw the error to be handled by the caller
  }
}

// Example Usage:
async function main() {
  const filePath = 'path/to/your/excel/file.xlsx'; // Replace with your file path
  const sheetName = 'Sheet1'; // Replace with the desired sheet name (optional)

  try {
    const excelData = await readExcel(filePath, sheetName);
    console.log('Excel Data:', excelData);
  } catch (error) {
    console.error("Failed to read Excel:", error.message); // Show the error message.
  }
}

main();
```

